const text = $("AI summarizer & translator").item.json.output; // Adjust field if your text is in a different property
const chunkSize = 4096;
let result = [];

for (let i = 0; i < text.length; i += chunkSize) {
  result.push({ json: { text: text.slice(i, i + chunkSize) } });
}

return result;
